mapper.select:
SELECT ROWNUM AS RN, T.*, C3.CONT_DESC AS JUDGE_DIS_NM, C1.CONT_DESC ENTRN_CLSS_NM, S1.NEIS_SCH_NM NEIS_SCHOOL_NM, C2.CONT_DESC JUPSU_AREA_NM FROM SUA_IPSI_MST T LEFT JOIN CMN_COM_CD C1 ON C1.LARGE_DIV = 'SUA10' AND T.ENTRN_CLSS_CD = C1.SMALL_DIV LEFT JOIN SUA_HIGH_SCHL S1 ON S1.NEIS_SCH_CD = T.NEIS_SCHOOL_CD LEFT JOIN CMN_COM_CD C2 ON C2.LARGE_DIV = 'SUA16' AND T.JUPSU_AREA = C2.SMALL_DIV LEFT JOIN CMN_COM_CD C3 ON C3.LARGE_DIV = 'SUA26' AND T.JUDGE_DIS = C3.SMALL_DIV WHERE T.ENTRN_YEAR = #{entrnYear}
AND T.ENTRN_CLSS_CD = #{entrnClssCd}
AND T.JUPSU_DT = #{jupsuDate}
AND T.SUHUM_NO = #{suhumNo}
AND T.SUHUM_NM LIKE '%' || #{suhumNm} || '%'
AND T.NEIS_SCHOOL_CD = #{schoolCode}

SELECT *
FROM SUA_DEPT_MST
WHERE ENTRN_YEAR = #{entrnYear}

AND ENTRN_CLSS_CD = #{entrnClssCd}

SELECT *
FROM SUA_JUNHYUNG
WHERE ENTRN_YEAR = #{entrnYear}

AND ENTRN_CLSS_CD = #{entrnClssCd}

AND WEB_GB = 'Y'
AND JUNHYUNG_CD <> '11'

SELECT *
FROM CMN_COM_CD
WHERE LARGE_DIV = #{largeDiv}
AND SMALL_DIV <> '00000000'

AND SMALL_DIV = #{smallDiv}


AND REL_CD3 = 'Y'
